package com.common.utils;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.ObjectInputStream;import java.io.ObjectOutputStream;/** * 序列化工具类 * @author 韩晓峰 * */public class SerializeUtil {	/**	 * //FIXME 改成最快的那个序列化工具类	 * 序列化成字节数组	 * @param object	 * @return	 */	public static byte[] serialize(Object object) {		ObjectOutputStream oos = null;		ByteArrayOutputStream baos = null;		try {			baos = new ByteArrayOutputStream();			oos = new ObjectOutputStream(baos);			oos.writeObject(object);			byte[] bytes = baos.toByteArray();			return bytes;		} catch (Exception e) {		}		return null;	}	/**	 * 反序列化字节数组	 * @param bytes	 * @return	 */	public static Object unserialize(byte[] bytes) {		ByteArrayInputStream bais = null;		try {			bais = new ByteArrayInputStream(bytes);			ObjectInputStream ois = new ObjectInputStream(bais);			return ois.readObject();		} catch (Exception e) {		}		return null;	}}